  nationTier: string;
  abbreviation: string;
  canSoloHost32: boolean;
  canSoloHost48: boolean;
  cohosts32: string[];
  cohosts48: string[];
  triHosts48: string[];
  quadHosts48: string[];
  emoji: string;
}

export interface GroupTeam {
  name: string;
  logo: string;
  abbreviation: string;
  region: RegionName;
  matchesPlayed: number;
  points: number;
  gDiff: number;
  gFor: number;
  gOpp: number;
  tier: string;
  nationTier: string;
  startingRating: {
    att: number;
    mid: number;
    def: number;
    pen: number;
  };
  dynamicRating: {
    att: number;
    mid: number;
    def: number;
    pen: number;
  };
  isBuffed: {
    att: boolean;
    mid: boolean;
    def: boolean;
    pen: boolean;
  };
  isDebuffed: {
    att: boolean;
    mid: boolean;
    def: boolean;
    pen: boolean;
  };
  currentBuffs: {
    att: Buff[];
    mid: Buff[];
    def: Buff[];
    pen: Buff[];
  };
  matchHistory: {
    qualifiers: {
      match: Match;
      opp: GroupTeam;
    }[];
    group: {
      match: Match;
      opp: GroupTeam;
    }[];
    bracket: {
      match: Match;
      opp: GroupTeam;
    }[];
  };
  rating: number;
  ranking: number;
  attRanking: number;
  midRanking: number;
  defRanking: number;
  reportCard: {
    grade: string | null;
    tournamentFinish: string | null;
    gradeStyle: string | null;
    gradeSummary: string | null;
  };
  emoji: string;
  homeTeam: boolean;
  canSoloHost32: boolean;
  canSoloHost48: boolean;
  cohosts32: string[];
  cohosts48: string[];
  triHosts48: string[];
  quadHosts48: string[];
  coach?: Person;
  pot?: number;
  groupFinish?: string;
}

export interface Buff {
  numOfGames: number;
  value: number;
}

export function isNation(value: unknown): value is Nation {
  return (<Nation>value).abbreviation !== undefined;
}

export const baseTeam = (hostNations: GroupTeam[], nation: Nation): GroupTeam => {
  // random nation values
  let min = 0;
  let max = 0;

  switch (nation.nationTier) {
    case 's':
      min = 80;
      max = 100;
      break;
    case 'a':
      min = 70;
      max = 95;
      break;
    case 'b':
      min = 65;
      max = 88;
      break;
    case 'c':
      min = 60;
      max = 88;
      break;
    case 'd':
      min = 55;
      max = 80;
      break;
    case 'e':
      min = 40;
      max = 78;
      break;
    case 'f':
      min = 30;
      max = 70;
      break;
    case 'g':
      min = 25;
      max = 55;
      break;
    default:
      min = 25;
      max = 55;
      break;
  }
  const attRating = getRandFloat(min, max);
  const midRating = getRandFloat(min, max);
  const defRating = getRandFloat(min, max);
  const penRating = getRandFloat(min, max);
  return {
    ...nation,
    gDiff: 0,
    gFor: 0,
    gOpp: 0,
    points: 0,
    matchesPlayed: 0,
    tier: nation.nationTier,
    startingRating: {
      att: attRating,
      mid: midRating,
      def: defRating,
      pen: penRating,
    },
    dynamicRating: {
      att: attRating,
      mid: midRating,
      def: defRating,
      pen: penRating,
    },
    isBuffed: {
      att: false,
      mid: false,
      def: false,
      pen: false,
    },
    isDebuffed: {
      att: false,
      mid: false,
      def: false,
      pen: false,
    },
    currentBuffs: {
      att: [],
      mid: [],
      def: [],
      pen: [],
    },
    rating: (attRating + midRating + defRating) / 3,
    ranking: 0,
    attRanking: 0,
    midRanking: 0,
    defRanking: 0,
    matchHistory: {
      qualifiers: [],
      group: [],
      bracket: [],
    },
    reportCard: {
      grade: null,
      gradeStyle: null,
      gradeSummary: null,
      tournamentFinish: null,
    },
    homeTeam: hostNations.map(t => t.name).includes(nation.name) ? true : false,
    region: nation.region as RegionName,
  };
};

