  'leader',
  'closer',
  'baron stealer',
];
export const AllRoles: Role[] = ['top', 'jungle', 'mid', 'adc', 'support'];
export function isRole(value: unknown): value is Role {
  return typeof value === 'string' && AllRoles.includes(value as Role);
}
