import { inject, Injectable } from '@angular/core';
import { getRandomInt } from '@shared/utils';
import { Nation } from 'app/models/nation.model';
import { Player } from '../../models/player.model';
import { Roster } from '../../models/roster.model';
import * as nationsJson from '../../../assets/json/nations.json';
import {
  addDoc,
  collection,
  CollectionReference,
  doc,
  DocumentData,
  DocumentReference,
  Firestore,
  limit,
  orderBy,
  Query,
  query,
  setDoc,
  updateDoc,
  where,
  WhereFilterOp,
} from '@angular/fire/firestore';

@Injectable({
  providedIn: 'root',
})
export class FirestoreService {
  db = inject(Firestore);
  nations = nationsJson;
  nationsList: Nation[];

  constructor() {
    this.nationsList = [];
    Object.values(this.nations).forEach(t => {
      if (t.nations) {
        this.nationsList.push(...(t.nations as Nation[]));
      }
    });
  }

  getFullName(nationality: string): (string | number | Query<DocumentData, DocumentData>)[] {
    const firstNameObj = this.getFirstNames(nationality);
    const lastNameObj = this.getLastNames(nationality, firstNameObj.ethnicity);
    return [...Object.values(firstNameObj), ...Object.values(lastNameObj)];
  }

  getFirstNames(nation: string): {
    nameQuery: Query<DocumentData, DocumentData>;
    ethnicity: string;
    totalNames: number;
  } {
    let randomNum = getRandomInt(1, 100);
    if (randomNum > 25) {
      randomNum = 0;
    } else if (randomNum > 10) {
      randomNum = 1;
    } else if (randomNum > 5) {
      randomNum = 2;
    } else if (randomNum > 2) {
      randomNum = 3;
    } else if (randomNum > 0) {
      randomNum = 4;
    }

    const usages = (this.nationsList.find(n => n.name === nation)?.firstNameUsages.length || 0 < 1
      ? this.nationsList.find(n => n.name === nation)?.firstNameUsages
      : ['English']) || ['English'];
    // if (nameOrigin.length === 1 || !nameOrigin) {
    //   console.log(nation, nameOrigin);
    // } else {
    //   console.log(nation, nameOrigin);
    // }

    let ethnicity: string;
    if (usages.length === 1 || randomNum > usages.length - 1) {
      [ethnicity] = usages;
    } else if (randomNum <= usages.length - 1) {
      ethnicity = usages[randomNum];
    } else if (usages.includes('Northern African')) {
      ethnicity = usages[1];
    } else {
      console.log('error with getFirstNames', nation, usages, randomNum);
      ethnicity = 'English';
    }

    switch (ethnicity) {
      case 'French':
      case 'Dutch':
      case 'Arabic':
      case 'Persian':
      case 'Kurdish': {
        // chance for 1-4 given names
        return this.nameRequest(getRandomInt(1, 4), 'firstNames_male', ethnicity);
      }
      case 'Western African': {
        // chance for 1-3 given names
        return this.nameRequest(getRandomInt(1, 3), 'firstNames_male', ethnicity);
      }
      case 'Russian':
      case 'Ukrainian':
      case 'Kazakh': {
        // first name and patronym
        return this.nameRequest(2, 'firstNames_male', ethnicity);
      }
      case 'Belarusian':
      case 'Indian':
      case 'English':
      case 'Irish':
      case 'Scottish':
      case 'Welsh':
      case 'Vietnamese':
      case 'German':
      case 'Polish':
      case 'Danish':
      case 'Norwegian':
      case 'Swedish':
      case 'Finnish':
      case 'Lithuanian':
      case 'Latvian':
      case 'Azerbaijani':
      case 'Eastern African':
      case 'Southern African':
      case 'Hebrew':
      case 'Turkmen':
      case 'Tajik':
      case 'Romanian':
      case 'Filipino':
      case 'Portuguese': {
        // one or two given names
        return this.nameRequest(getRandomInt(1, 2), 'firstNames_male', ethnicity);
      }
      case 'Estonian':
      case 'Turkish':
      case 'Spanish':
      case 'Galician':
      case 'Basque':
      case 'Catalan': {
        // one or two given names where one is more common
        const chance = getRandomInt(1, 10);
        let names = 0;
        if (chance > 8) {
          names = 2;
        } else {
          names = 1;
        }
        return this.nameRequest(names, 'firstNames_male', ethnicity);
      }

      default: {
        // only first name (Czech, Slovak, Polish, Bosnian, Serbian, Croatian, Montenegrin, Albanian, Slovene, Macedonian (male-ending),
        // Chinese, Japanese, Korean, Icelandic, Faroese, Malay, Italian, Kyrgyz, Georgian, Armenian, Bulgarian, Uzbek (if no patronym), Hungarian, Greek, Thai)
        return this.nameRequest(1, 'firstNames_male', ethnicity);
      }
    }
  }

  getLastNames(
    nation: string,
    ethnicity: string
  ): {
    nameQuery: Query<DocumentData, DocumentData>;
    ethnicity: string;
    totalNames: number;
  } {
    const nameOrigin = this.nationsList.find(n => n.name === nation)?.lastNameUsages || ['English'];

    let origin = ethnicity;

    if (nation === 'Argentina') {
      // 1-2 surname countries
      return this.nameRequest(getRandomInt(1, 2), 'lastNames', origin);
    }

    if (!nameOrigin.includes(origin)) {
      let randomNum = getRandomInt(1, 100);
      if (randomNum > 25) {
        randomNum = 0;
      } else if (randomNum > 10) {
        randomNum = 1;
      } else if (randomNum > 5) {
        randomNum = 2;
      } else if (randomNum > 2) {
        randomNum = 3;
      } else if (randomNum > 0) {
        randomNum = 4;
      }

      if (randomNum > nameOrigin.length - 1) {
        [origin] = nameOrigin;
      } else {
        origin = nameOrigin[randomNum];
      }
    }

    switch (origin) {
      case 'Spanish':
      case 'Basque':
      case 'Galician':
      case 'Catalan':
      case 'Bulgarian':
      case 'Filipino':
      case 'Spanish (Philippines)': {
        // 2 surname countries
        // need to fix bulgarian names to not include feminine surnames
        return this.nameRequest(2, 'lastNames', origin);
      }
      case 'Portuguese': {
        // 1-4 surname countries e.g. Portuguese
        let names = 1;
        const chance = getRandomInt(1, 10);

        if (chance < 3) {
          names = 1;
        } else if (chance > 8) {
          names = 4;
        } else {
          names = getRandomInt(2, 3);
        }
        return this.nameRequest(names, 'lastNames', origin);
      }
      case 'Icelandic':
      case 'Faroese':
      case 'Malay':
      case 'Kyrgyz':
      case 'Azerbaijani':
      case 'Eastern African': {
        // nordic/scandinavian/malay/kyrgyz/azerbaijani/eastern african patronym
        return this.nameRequest(1, 'firstNames_male', origin);
      }
      default:
        // just one last name (most countries)
        // need more surnames for pacific islanders
        return this.nameRequest(1, 'lastNames', origin);
    }
  }

  nameRequest(
    totalNames: number,
    nameCollection: string,
    ethnicity: string,
    operator: WhereFilterOp = '>='
  ): {
    nameQuery: Query<DocumentData, DocumentData>;
    ethnicity: string;
    totalNames: number;
  } {
    const randomIndex = getRandomInt(1, 5);
    // changed this to 5,000 from 0, 50,000 in order to fix, need to change back
    const randomQuery = getRandomInt(0, 50000);
    const nameQuery = this.newRequest(totalNames, nameCollection, ethnicity, randomIndex, randomQuery, operator);
    return { nameQuery, ethnicity, totalNames };
  }

  saveRoster(uid: string, saveName: string, benchReserves: Player[], starters: Player[], nationOrTier: string) {
    const rosterRef = doc(this.db, 'users', uid, 'savedRosters');
    return setDoc(rosterRef, {
      benchReserves,
      starters,
      nationOrTier,
      saveName,
    });
  }

  updateRoster(uid: string, saveName: string, benchReserves: Player[], starters: Player[], firestoreId: string) {
    const rosterRef = doc(this.db, 'users', uid, 'savedRosters', firestoreId);
    updateDoc(rosterRef, {
      saveName,
      benchReserves,
      starters,
    }).then(() => {
      console.log('Document Updated');
    });
  }

  submitRoster(roster: Roster) {
    if (roster.id === '') {
      const rostersCollection = collection(this.db, 'submittedRosters');
      addDoc(rostersCollection, {
        user: roster.user,
        roster: roster.players,
        tier: roster.tier,
        nation: roster.nation,
        squadRating: roster.squadRating,
        startersRating: roster.startersRating,
        formation: roster.formation,
      }).then(docRef => {
        console.log('New firestore id', docRef.id);
        return docRef.id;
      });
    } else {
      const rosterRef = doc(this.db, 'submittedRosters', roster.id);
      setDoc(rosterRef, {
        user: roster.user,
        roster: roster.players,
        tier: roster.tier,
        nation: roster.nation,
        squadRating: roster.squadRating,
        startersRating: roster.startersRating,
        formation: roster.formation,
      });
    }
  }

  // ============ THE BELOW FUNCTIONS NEED TO BE LISTENED TO USING THE onSnapshot() function

  newRequest(
    totalNames: number,
    collectionPath: string,
    ethnicity: string,
    randomIndex: number,
    randomQuery: number,
    operator: WhereFilterOp
  ): Query<DocumentData, DocumentData> {
    if (!totalNames || !collectionPath || !ethnicity || !randomIndex || !randomQuery || !operator) {
      console.log('error calling new request', totalNames, collectionPath, ethnicity, randomIndex, randomQuery, operator);
    }
    const collectionRef = collection(this.db, collectionPath);
    const nameQuery = query(
      collectionRef,
      where(`randomNum.${randomIndex}`, operator, randomQuery),
      where('usages', 'array-contains-any', [ethnicity]),
      orderBy(`randomNum.${randomIndex}`),
      limit(totalNames)
    );
    return nameQuery;
  }

  getSubmittedRosters(): Query<DocumentData, DocumentData> {
    const collectionRef = collection(this.db, 'submittedRosters');
    const top50Rosters = query(collectionRef, orderBy('startersRating', 'desc'), limit(50));
    return top50Rosters;
  }

  getRosterId(uid: string): CollectionReference<DocumentData, DocumentData> {
    return collection(this.db, 'users', uid, 'savedRosters');
  }

  getRoster(uid: string, rosterId: string): DocumentReference<DocumentData, DocumentData> {
    return doc(this.db, 'users', uid, 'savedRosters', rosterId);
  }
}

import { Injectable } from '@angular/core';
import { Person } from 'app/models/player.model';

export interface LeaderboardItem {
  time: string;
  tournament: {
    winner: string;
    winnerRank: number;
    worst: string;
    worstRank: number;
    second: string;
    third: string;
  };
}

@Injectable({
  providedIn: 'root',
})
export class LeaderboardService {
  constructor() {}

  fetchLocalStorage(): LeaderboardItem[] | null {
    const item = localStorage.getItem('tournamentSubmitted24Hours');
    return item ? JSON.parse(item) : null;
  }

  saveLocalStorage(id: string, arr: (LeaderboardItem | Person)[]) {
    const item = JSON.stringify(arr);
    localStorage.setItem(id, item);
  }

  fetchLocalNames(): Person[] | null {
    const item = localStorage.getItem('names');
    return item ? JSON.parse(item) : null;
  }

  fetchTournamentLeaderboards() {
    const personalLeaderboards = this.fetchPersonalLeaderboards();
    const worldLeaderboards = this.fetchWorldLeaderboards();
    return {
      worldLeaderboards,
      personalLeaderboards,
    };
  }

  fetchPersonalLeaderboards() {
    const localItems = this.fetchLocalStorage();
    return localItems?.slice(0, 10) || null;
  }

  fetchWorldLeaderboards() {
    return null;
  }
}

import { Injectable } from '@angular/core';

export type MobaStorageKey = 'moba_region' | 'moba_player_options' | 'moba_selected_players';
export type MobaDraftKey = 'draft_metaData';

@Injectable({
  providedIn: 'root',
})
export class MobaService {
  constructor() {}

  setLocalStorage<T>(key: MobaStorageKey | MobaDraftKey, value: T) {
    localStorage.setItem(key, JSON.stringify(value));
  }

  getLocalStorage<T>(key: MobaStorageKey | MobaDraftKey): T | '' {
    const item = localStorage.getItem(key);
    return item ? JSON.parse(item) : '';
  }

  removeLocalStorage(key: MobaStorageKey | MobaDraftKey) {
    localStorage.removeItem(key);
  }
}

import { Injectable } from '@angular/core';
import { UntilDestroy, untilDestroyed } from '@ngneat/until-destroy';
import { getRandomInt, groupByProp } from '@shared/utils';
import { GroupTeam } from 'app/models/nation.model';
import { get } from 'lodash-es';
import { BehaviorSubject } from 'rxjs';
import { drawAndSort } from './group-draw.utils';
import { Match, Region, RegionName, TeamsByRegion, Tournament } from './simulation.model';
import { SimulationService } from './simulation.service';
import { extraTimeResult, matchScore, regionQualifierHelper } from './simulation.utils';

@UntilDestroy()
@Injectable({
  providedIn: 'root',
})
export class SimulationQualifiersService {
  simulator: SimulationService;
  extraTeams = 0;
  drawData$: BehaviorSubject<GroupTeam[][]> = new BehaviorSubject([] as GroupTeam[][]);
  tournament: Tournament | null = null;
  constructor(simulator: SimulationService) {
    this.simulator = simulator;

    this.simulator.tournament$.pipe(untilDestroyed(this)).subscribe(t => (this.tournament = t));
  }

  chooseQualifyingTeams(
    regionsSelected: Region[],
    numberOfTeams: 32 | 48,
    nationsList: GroupTeam[],
    hostNations: GroupTeam[]
  ): GroupTeam[] {
    // resets
    // create a list of the teams from each region and choose random teams from each region
    // to take up all the spots that are alloted at the world cup for each region
    // this should be realistic based on the number of teams in the tournament
    // console.log('TESTS\n\n\n\n\n\n\n\n\n\n\nCHOOSE QUALIFYING1', numberOfTeams);
    return this.tournamentFormat(regionsSelected, nationsList, hostNations, numberOfTeams);
  }

  organizeGroups(teams: GroupTeam[], teamsInGroup: number, numberOfTeams: number, hostNations: GroupTeam[], availableRegions: Region[]) {
    this.potDraw(teams, teamsInGroup, hostNations, availableRegions);
  }

  tournamentFormat(regions: Region[], nationsList: GroupTeam[], hostNations: GroupTeam[], numberOfTeams: 32 | 48): GroupTeam[] {
    console.log('TESTING BUG', numberOfTeams, typeof numberOfTeams);
    const teamsQualified: GroupTeam[] = [];
    const regionValues = regions.map(r => r.value);

    const nationsLeft: GroupTeam[] = nationsList.filter(team => regionValues.includes(team.region)).sort((a, b) => b.rating - a.rating);

    hostNations.forEach((nation, i) => {
      const host = nationsLeft.findIndex(t => t.name === nation.name);
      teamsQualified.push(nationsLeft.splice(host, 1)[0]);
      console.log(teamsQualified[i].name, teamsQualified[i].ranking, 'qualifies as host');
    });

    const cafHosts = hostNations.filter(h => h.region === RegionName.caf).length;
    const afcHosts = hostNations.filter(h => h.region === RegionName.afc).length;
    const ofcHosts = hostNations.filter(h => h.region === RegionName.ofc).length;
    const uefaHosts = hostNations.filter(h => h.region === RegionName.uefa).length;
    const concacafHosts = hostNations.filter(h => h.region === RegionName.concacaf).length;
    const conmebolHosts = hostNations.filter(h => h.region === RegionName.conmebol).length;

    const autoQualifying48 = {
      uefa: 12 - uefaHosts,
      conmebol: 6 - conmebolHosts,
      caf: 9 - cafHosts,
      afc: 8 - afcHosts,
      concacaf: ofcHosts === 4 ? 5 : 6 - concacafHosts,
      ofc: 0 - ofcHosts,
    };

    const autoQualifying32 = {
      uefa: 10,
      conmebol: 4,
      caf: 0,
      afc: 4,
      concacaf: 3,
      ofc: 0,
    };

    const autoQualifyingSpots = numberOfTeams === 48 ? autoQualifying48 : autoQualifying32;

    // three options
    // 1. one region that has all the teams
    // x = region.numberOfTeams - 32, top ${32 - x - 1} and host qualify auto, x number of playoffs to determine rest
    if (regions.length === 1) {
      const [region] = regions;
      const playoffMatches = region.numOfTeams - numberOfTeams;
      this.extraTeams = playoffMatches;
      const qualifyingSpots = numberOfTeams - playoffMatches - hostNations.length;
      if (Math.random() > 0.8) {
        const team = nationsLeft.splice(getRandomInt(1, qualifyingSpots), 1)[0];
        nationsLeft.splice(numberOfTeams - playoffMatches, 0, team);
      }
      console.log(
        `qualified automatically for World Cup from ${region.label}`,
        hostNations.length > 1
          ? teamsQualified.slice(0, hostNations.length).map(t => `${t.name} ${t.ranking}`)
          : `${teamsQualified[0].name} ${teamsQualified[0].ranking}`,
        nationsLeft.slice(0, qualifyingSpots).map(a => `${a.name} ${a.ranking}`)
      );
      console.log(
        `didn't qualify automatically from ${region.label}`,
        nationsLeft.slice(qualifyingSpots).map(t => `${t.name}-${t.ranking}`)
      );
      teamsQualified.push(...nationsLeft.slice(0, qualifyingSpots));

      const qualifiers = this.autoBracketQualifiers(playoffMatches, nationsLeft, qualifyingSpots, region);

      teamsQualified.push(...qualifiers.map(m => m.winner));

      return teamsQualified;
    }
    // 2. multiple regions but not all the regions
    else if (regions.length < 5 || (regionValues.includes(RegionName.ofc) && regions.length === 5)) {
      const numOfNationsLeft = nationsLeft.length;
      const ratio = numOfNationsLeft / (numberOfTeams - hostNations.length);
      const teamsByRegion: TeamsByRegion = groupByProp(nationsLeft, 'region');
      // console.log(teamsByRegion, 'Available Nations', numOfNationsLeft, 'Ratio', ratio);
      // ====== Automatic Qualifiers =======
      // assign qualifying spot to each region based on the total number of teams and then dividing by the same ratio
      Object.entries(teamsByRegion).forEach(([region, nations]: [string, GroupTeam[]]) => {
        const nationsInRegion = nations.length;
        // const hosts = hostNations.filter(h => h.region === region).length;
        const qualifyingSpots = Math.floor(nationsInRegion / ratio);
        const extraSpots = Math.round(nationsInRegion % ratio);
        console.log(
          region.toLocaleUpperCase(),
          '\n',
          '# of nations from region',
          nationsInRegion,
          '\n',
          'Qualifying spots from this region',
          qualifyingSpots,
          '\n',
          'Extra spots available for this region',
          extraSpots
        );
        const index = regions.findIndex(r => r.value === region);
        regions[index].qualifiers = {
          auto: qualifyingSpots,
          extra: extraSpots,
        };
        teamsQualified.push(...nations.slice(0, qualifyingSpots));
        console.log(
          `qualified automatically for World Cup from ${region.toLocaleUpperCase()}`,
          nations.slice(0, regions[index].qualifiers.auto).map(a => `${a.name} ${a.ranking}`)
        );
        // console.log(
        //   `didn't qualify automatically from ${region.toLocaleUpperCase()}`,
        //   nations.slice(regions[index].qualifiers.auto).map(t => `${t.name}-${t.ranking}`)
        // );
      });

      // console.log(
      //   '# automatically qualified spots',
      //   regions.reduce((a, b) => a + b.qualifiers.auto, 0) + 1,
      //   '# of extra spots',
      //   regions.reduce((a, b) => a + b.qualifiers.extra, 0),
      //   teamsQualified
      // );
      // ====== Playoff Qualifiers =======
      let numQualified = teamsQualified.length;
      Object.entries(teamsByRegion).forEach(([region, nations]: [string, GroupTeam[]]) => {
        const index = regions.findIndex(r => r.value === region);
        const extra =
          regions[index].qualifiers.extra + numQualified <= numberOfTeams ? regions[index].qualifiers.extra : numberOfTeams - numQualified;
        const auto = regions[index].qualifiers.auto;
        if (numQualified === numberOfTeams || extra === 0) {
          return;
        }
        const qualifiers = this.autoBracketQualifiers(extra, nations, auto, regions[index]);
        teamsQualified.push(...qualifiers.map(m => m.winner));
        numQualified += extra;
        this.extraTeams += extra;
      });
      return teamsQualified;
    }
    // 3. and the normal version with all the regions available
    else {
      this.extraTeams = 2;
      const teamsByRegion: TeamsByRegion = groupByProp(nationsLeft, 'region');
      // console.log(teamsByRegion, nationsLeft);

      Object.entries(teamsByRegion).forEach(([region, nations]: [string, GroupTeam[]]) => {
        const qualifyingSpot = get(autoQualifyingSpots, region, 0);
        if (qualifyingSpot === 0) {
          return;
        }
        console.log(
          `qualified automatically for World Cup from ${region.toLocaleUpperCase()}`,
          teamsQualified[0].region === region ? [`${teamsQualified[0].name} ${teamsQualified[0].ranking}`] : '',
          nations.slice(0, qualifyingSpot).map(a => `${a.name} ${a.ranking}`)
        );
        // console.log(
        //   `didn't qualify automatically from ${region.toLocaleUpperCase()}`,
        //   nations.slice(qualifyingSpot).map(t => `${t.name}-${t.ranking}`)
        // );
        teamsQualified.push(...nations.slice(0, qualifyingSpot));
      });
      // console.log('TESTS\n\n\n\n\n\n\n\n\n\n\n');
      return numberOfTeams === 48
        ? this.regionSpecificQualifiers48(teamsQualified, teamsByRegion, regions, hostNations, autoQualifyingSpots)
        : this.regionSpecificQualifiers32(teamsQualified, teamsByRegion, regions, hostNations);
    }
  }

  autoBracketQualifiers(
    matches: number,
    availableNations: GroupTeam[],
    alreadyQualified: number,
    region: Region | 'inter',
    isFinalQualifier = true
  ): Match[] {
    const qualifiers: Match[] = [];
    for (let i = 0; i < matches; i++) {
      const wtIndex = alreadyQualified + (matches * 2 - 1 - i);
      qualifiers.push(matchScore(availableNations[alreadyQualified + i], availableNations[wtIndex], true));
    }
    qualifiers.forEach(match => {
      match.winner.matchHistory.qualifiers.push({ match, opp: match.loser });
      match.loser.matchHistory.qualifiers.push({ match, opp: match.winner });
      console.log(
        `${region === 'inter' ? 'inter-confederation' : region.label + ' qualifier'} playoff where ${match.winner.name} defeated ${
          match.loser.name
        } with a score of ${match.score}${extraTimeResult(match)}`
      );
    });
    if (isFinalQualifier && matches > 0) {
      console.log(
        `qualified from ${region === 'inter' ? 'inter-confederation' : region.label + ' via'} playoff`,
        qualifiers.map(a => `${a.winner.name} ${a.winner.ranking}`)
      );
      console.log(
        `didn't qualify from ${region === 'inter' ? 'inter-confederation' : region.label + ' via'} playoff`,
        qualifiers.map(t => `${t.loser.name} ${t.loser.ranking}`)
      );
    }
    return qualifiers;
  }

  regionSpecificQualifiers48(
    teamsQualified: GroupTeam[],
    teamsByRegion: TeamsByRegion,
    regions: Region[],
    hostNations: GroupTeam[],
    alreadyQualified: {
      uefa: number;
      caf: number;
      afc: number;
      concacaf: number;
      ofc: number;
      conmebol: number;
    }
  ): GroupTeam[] {
    // ====== Playoff Qualifiers =======
    // ====== Confederation Qualifiers ======

    const numOfTeams = 48;
    const { uefa, afc, caf, concacaf, conmebol, ofc } = teamsByRegion;
    if (caf && uefa && afc && concacaf && conmebol && ofc) {
      // ====== CAF Qualifiers ======
      // REAL LIFE QUALIFIER FORMAT:
      // 9 groups of 6 teams : round robin matches of 10 total (5 home, 5 away) : top team from each qualifies (9)
      // 4 best runner ups in a 2 round tournament : for 1 spot in inter-confederation play-off (+1)
      const cafQualified = alreadyQualified.caf;
      const { matches: cafMatches, matches2: cafMatches2 } = regionQualifierHelper(regions[1], numOfTeams, hostNations);
      const cafFirstRound = this.autoBracketQualifiers(cafMatches, caf, cafQualified, regions[1], false);
      const cafWinners = cafFirstRound.map(m => m.winner);
      const cafQualifiers = this.autoBracketQualifiers(cafMatches2, cafWinners, 0, regions[1]);
      const cafTeams = cafQualifiers.map(m => m.winner);

      // ===== UEFA Qualifiers =====
      // REAL LIFE QUALIFIER FORMAT:
      // 1ST ROUND -- All teams compete in 12 groups of 4-5 teams (8-10 matches each team) : winners qualify automatically (12)
      // 2ND ROUND -- 12 runner ups and 4 best nation league teams compete in a 2 round tournament : for 4 spots to qualify (4)

      const uefaQualified = alreadyQualified.uefa;
      const { matches: uefaMatches, matches2: uefaMatches2 } = regionQualifierHelper(regions[0], numOfTeams, hostNations);

      const uefaFirstRound = this.autoBracketQualifiers(uefaMatches, uefa, uefaQualified, regions[0], false);
      const uefaWinners = uefaFirstRound.map(m => m.winner);
      const uefaQualifiers = this.autoBracketQualifiers(uefaMatches2, uefaWinners, 0, regions[0]);

      teamsQualified.push(...uefaQualifiers.map(m => m.winner));

      // ===== AFC Qualifier =====
      // REAL LIFE QUALIFIER FORMAT:
      // 3RD ROUND -- 18 teams compete in 3 groups of 6 teams (10 matches each team) : top 2 spots of each group qualify automatically (6)
      // 4TH ROUND -- 6 leftover teams compte in 2 groups of 3 teams round robin (2 matches each team) : top spot qualifies (2)
      // 5TH ROUND -- Two 2nd place teams face in a 2 legged playoff tie to make inter-confederation-playoffs (+1)
      const afcQualified = alreadyQualified.afc;
      const { matches: afcMatches } = regionQualifierHelper(regions[2], numOfTeams, hostNations);
      const afcQualifiers = this.autoBracketQualifiers(afcMatches, afc, afcQualified, regions[2], false);
      const afcTeams = afcQualifiers.map(m => m.winner);

      // ===== OFC Qualifier =====
      // REAL LIFE QUALIFIER FORMAT:
      // 2ND ROUND -- 8 teams compete in 2 groups of 4 teams (3 or 6 matches - TBD) : top 2 spots of each group advance rounds
      // 3RD ROUND -- 4 teams compete in a bracket (2 matches each team) : top spot qualifies (1+1)
      const ofcQualified = alreadyQualified.ofc < -1 ? Math.abs(alreadyQualified.ofc) : 0;
      const ofcTeams: GroupTeam[] = [];
      if (ofcQualified === 0) {
        const { matches: ofcMatches, matches2: ofcMatches2 } = regionQualifierHelper(regions[5], numOfTeams, hostNations);
        const ofcFirstRound = this.autoBracketQualifiers(ofcMatches, ofc, ofcQualified, regions[5], false);
        const ofcWinners = ofcFirstRound.map(m => m.winner);
        const ofcFinalists = this.autoBracketQualifiers(ofcMatches2, ofcWinners, 0, regions[5]);
        if (alreadyQualified.ofc === -1) {
          ofcTeams.push(...ofcFinalists.map(m => m.winner));
        } else {
          teamsQualified.push(...ofcFinalists.map(m => m.winner));
          ofcTeams.push(...ofcFinalists.map(m => m.loser));
        }
      }

      // ===== CONCACAF Qualifier =====
      // REAL LIFE QUALIFIER FORMAT:
      // 3RD ROUND -- 3 groups of 4 teams compete in a round robin (6 matches each team) : top from each group qualifies and best 2 runner ups go to inter-confederation playoffs (3+2)
      const concacafQualified = alreadyQualified.concacaf;
      // const { matches: concacafMatches } = regionQualifierHelper(regions[3], numOfTeams, hostNations);
      const concacafTeams = concacaf.slice(concacafQualified, concacafQualified + 2);
      // const concacafQualifiers = this.autoBracketQualifiers(concacafMatches, afc, concacafQualified, regions[3]);
      // teamsQualified.push(...concacafQualifiers.map(m => m.winner));

      // ===== CONMEBOL Qualifier =====
      // REAL LIFE QUALIFIER FORMAT:
      // 1ST ROUND -- All 10 teams compete in a round robin group (18 matches each) : top 6 qualify and 7th goes to inter-confederation playoffs (6+1)
      const conmebolQualified = alreadyQualified.conmebol;
      const conmebolTeam = conmebol[conmebolQualified];

      // ===== Inter-confederation Qualifiers =====
      const sortedInterTeams = [conmebolTeam, ...concacafTeams, ...ofcTeams, ...afcTeams, ...cafTeams].sort((a, b) => b.rating - a.rating);
      if (ofcQualified < 2) {
        // 0 - 1 OFC HOSTS
        const interFirstRound = this.autoBracketQualifiers(2, sortedInterTeams, 2, 'inter', false);
        const interWinners = interFirstRound.map(m => m.winner);
        // TOP 2 rated teams get a bye to second round
        const interSecondRoundTeams = [...interWinners, ...sortedInterTeams.slice(0, 2)].sort((a, b) => b.rating - a.rating);
        const interSecondRound = this.autoBracketQualifiers(2, interSecondRoundTeams, 0, 'inter');
        teamsQualified.push(...interSecondRound.map(m => m.winner));
      } else if (ofcQualified === 2) {
        // 2 OFC HOSTS
        // 1 TEAM QUALIFIES INTERNATIONALLY
        // TOP rated team gets a bye to third round
        const interFirstRound = this.autoBracketQualifiers(2, sortedInterTeams, 1, 'inter', false);
        const interWinners = interFirstRound.map(m => m.winner);
        const interSecondRound = this.autoBracketQualifiers(1, interWinners, 0, 'inter', false);
        const interThirdRoundTeams = [sortedInterTeams[0], ...interSecondRound.map(m => m.winner)];
        const interThirdRound = this.autoBracketQualifiers(1, interThirdRoundTeams, 0, 'inter', true);
        teamsQualified.push(...interThirdRound.map(m => m.winner));
      }
      // 3-4 OFC HOSTS
      // 0 TEAM QUALIFIES INTERNATIONALLY
    }

    return teamsQualified;
  }

  regionSpecificQualifiers32(
    teamsQualified: GroupTeam[],
    teamsByRegion: TeamsByRegion,
    regions: Region[],
    hostNations: GroupTeam[]
  ): GroupTeam[] {
    // ====== Playoff Qualifiers =======
    // ====== Confederation Qualifiers ======

    const { uefa, afc, caf, concacaf, conmebol, ofc } = teamsByRegion;
    const hostRegions = hostNations.map(h => h.region);
    let hasFirstPlayoff = true;
    let hasSecondPlayoff = true;
    if (caf) {
      // ====== CAF Qualifiers ======
      const multiHosts = hostNations.length > 1 && hostRegions.includes(RegionName.caf);
      const matches = multiHosts ? 4 : 5;
      const alreadyQualified = 0;
      const region = regions[1];

      const cafQualifiers = this.autoBracketQualifiers(matches, caf, alreadyQualified, region);

      teamsQualified.push(...cafQualifiers.map(m => m.winner));

      console.log(
        `qualified from ${cafQualifiers[0].winner.region}`,
        cafQualifiers.map(a => `${a.winner.name} ${a.winner.ranking}`)
      );
      console.log(
        `didn't qualify from ${cafQualifiers[0].loser.region}`,
        caf.slice(matches * 2).map(t => `${t.name} ${t.ranking}`),
        cafQualifiers.map(t => `${t.loser.name}-${t.loser.ranking}`)
      );
    }
    if (uefa) {
      // ===== UEFA Qualifiers =====
      const doubleHosts = hostNations.length === 2 && hostNations.every(n => n.region === RegionName.uefa);
      const matches = doubleHosts ? 4 : 6;
      const matches2 = doubleHosts ? 2 : 3;
      const alreadyQualified = 10;
      const region = regions[0];

      const uefaFirstRound = this.autoBracketQualifiers(matches, uefa, alreadyQualified, region, false);
      const winners = uefaFirstRound.map(m => m.winner);
      const uefaQualifiers = this.autoBracketQualifiers(matches2, winners, 0, region);

      teamsQualified.push(...uefaQualifiers.map(m => m.winner));
      // console.log(
      //   `didn't qualify from ${uefaQualifiers[0].loser.region}`,
      //   uefa.slice(22).map(t => `${t.name}-${t.ranking}`),
      //   uefaFirstRound.map(t => `${t.loser.name}-${t.loser.ranking}`),
      //   uefaQualifiers.map(t => `${t.loser.name}-${t.loser.ranking}`)
      // );
    }
    if (afc && conmebol && concacaf) {
      // ===== AFC Qualifier =====
      const matches =
        (hostNations.length === 2 && hostNations.every(n => n.region === RegionName.afc)) ||
        (hostRegions.includes(RegionName.uefa) && hostRegions.includes(RegionName.afc))
          ? 0
          : 1;
      const alreadyQualified = 4;
      const region = regions[2];
      const afcQualifiers: (Match | undefined)[] = this.autoBracketQualifiers(matches, afc, alreadyQualified, region, false);
      const ofcQualifiers: (Match | undefined)[] = [];
      if (ofc) {
        const ofcMatches = hostRegions.includes(RegionName.afc) && hostRegions.includes(RegionName.ofc) ? 0 : 1;
        const ofcQualified = 0;
        const ofcRegion = regions[5];
        ofcQualifiers.push(...this.autoBracketQualifiers(ofcMatches, ofc, ofcQualified, ofcRegion, false));
      }
      // ===== Inter-confederation Qualifiers =====
      if (afcQualifiers[0] === undefined) {
        hasFirstPlayoff = false;
        // console.log(`${conmebol[4].name} automatically qualified from CONMEBOL via bye`);
      } else if (hostNations.length === 2 && hostNations.every(n => n.region === RegionName.conmebol)) {
        hasFirstPlayoff = false;
        // console.log(`${afcQualifiers[0].winner.name} automatically qualified from AFC via bye`);
      } else {
        const playoff1 = matchScore(afcQualifiers[0].winner, conmebol[4], true);
        playoff1.winner.matchHistory.qualifiers.push({
          match: playoff1,
          opp: playoff1.loser,
        });
        playoff1.loser.matchHistory.qualifiers.push({
          match: playoff1,
          opp: playoff1.winner,
        });
        teamsQualified.push(playoff1.winner);
        console.log(
          `playoff between ${afcQualifiers[0].winner.name} and ${conmebol[4].name} resulted in a win for ${playoff1.winner.name}
        with a score of ${playoff1.goalsFor}-${playoff1.goalsAg}${extraTimeResult(playoff1)}`
        );
      }

      if (
        (hostNations.length === 2 && hostNations.every(n => n.region === RegionName.concacaf)) ||
        (ofcQualifiers[0] === undefined && ofc)
      ) {
        hasSecondPlayoff = false;
      } else if (!ofc || ofcQualifiers[0] === undefined) {
        teamsQualified.push(concacaf[3]);
        console.log(`${concacaf[3].name} automatically qualified from CONCACAF via bye`);
      } else {
        const playoff2 = matchScore(ofcQualifiers[0].winner, concacaf[3], true);
        playoff2.winner.matchHistory.qualifiers.push({
          match: playoff2,
          opp: playoff2.loser,
        });
        playoff2.loser.matchHistory.qualifiers.push({
          match: playoff2,
          opp: playoff2.winner,
        });
        teamsQualified.push(playoff2.winner);
        console.log(
          `playoff between ${concacaf[3].name} and ${ofcQualifiers[0].winner.name} resulted in a win for ${playoff2.winner.name}
        with a score of ${playoff2.goalsFor}-${playoff2.goalsAg}${extraTimeResult(playoff2)}`
        );
      }
    }
    if (hasFirstPlayoff && hasSecondPlayoff) {
      console.log(
        teamsQualified.slice(-1)[0].name,
        'and',
        teamsQualified.slice(-2, -1)[0].name,
        'qualify via inter-confederation play-off'
      );
    } else {
      console.log(teamsQualified.slice(-1)[0].name, 'qualify via inter-confederation play-off');
    }

    console.log('TESTESTSDFDSFDSFSDFSDFDSFSDF', teamsQualified.length, teamsQualified);
    return teamsQualified;
  }

  potDraw(teams: GroupTeam[], teamsInGroup: number, hostNations: GroupTeam[], availableRegions: Region[]) {
    console.log('TESTSETSTST', groupByProp(teams, 'region'), teams.length);
    const pots = teamsInGroup;
    const teamsInPot = teams.length / pots;
    const extraTeams = this.extraTeams;
    // ====== remove playoff teams and put them at the back and sort teams by rating
    const playoffTeams = teams.splice(-extraTeams, extraTeams);
    const hosts = teams.splice(0, hostNations.length);
    teams.sort((a, b) => b.rating - a.rating);
    teams.push(...playoffTeams);
    // ======= assign teams to pots ===========

    const potTeams: GroupTeam[][] = [];
    let index = teamsInPot - hostNations.length;
    for (let i = 0; i < pots; i++) {
      if (i === 0) {
        potTeams.push([...hosts, ...teams.slice(0, index)]);
      } else {
        potTeams.push(teams.slice(index, index + teamsInPot));
        index += teamsInPot;
      }
    }
    console.log(
      'potTeams',
      potTeams.map(g => g.map(t => `${t.name} ${t.region}`))
    );
    const start = Date.now();

    if (typeof Worker !== 'undefined') {
      // Create a new
      const worker = new Worker(new URL('./simulation.worker', import.meta.url));
      worker.onmessage = ({ data }) => {
        this.simulator.tournament$.next({ groups: data.draw });

        this.simulator.isLoading$.next(false);
      };
      this.simulator.isLoading$.next(true);
      worker.postMessage({ potTeams, teamsInPot, availableRegions, start, hosts });
    } else {
      console.log('test: worker not working');

      this.simulator.tournament$.next({
        groups: drawAndSort(
          potTeams,
          hosts.map(h => h.name),
          teamsInPot,
          availableRegions
        ),
      });
    }
  }
}

import { DestroyRef, Injectable, inject } from '@angular/core';
import { compare } from '@shared/utils';
import { findTeamInTournament, matchScore, roundOf32Helper } from './simulation.utils';
import { GroupTeam } from 'app/models/nation.model';
import { KnockoutRound, Region, Tournament } from './simulation.model';
import { BehaviorSubject } from 'rxjs';
import { CreatePlayerService } from '@core/services/create-player.service';
import { takeUntilDestroyed } from '@angular/core/rxjs-interop';

@Injectable({
  providedIn: 'root',
})
export class SimulationService {
  createPerson: CreatePlayerService;
  selectedNation$ = new BehaviorSubject<GroupTeam | null>(null);
  tournament$ = new BehaviorSubject<Tournament | null>(null);
  isLoading$ = new BehaviorSubject<boolean>(true);
  tournament: Tournament | null = null;
  hostNations?: GroupTeam[];
  destroyRef = inject(DestroyRef);

  constructor(createPerson: CreatePlayerService) {
    this.tournament$.pipe(takeUntilDestroyed(this.destroyRef)).subscribe(t => {
      this.tournament = t;
      this.hostNations = t?.hostNations;
    });

    this.createPerson = createPerson;
  }

  checkForApp() {
    // not ideal to type this as any, but no choice until I find a better solution
    // eslint-disable-next-line @typescript-eslint/no-explicit-any
    if ((window.navigator as any).standalone === true) {
      console.log('iOS app');
      return true;
    } else if (window.matchMedia('(display-mode: standalone)').matches) {
      console.log('android app');
      return true;
    }
    return false;
  }

  changeSelectedNation(value: null | GroupTeam) {
    if (!this.tournament?.groups || !value) {
      this.selectedNation$.next(value);
      return;
    }
    const updatedNation = findTeamInTournament(this.tournament?.groups, value);
    if (updatedNation) {
      this.selectedNation$.next(updatedNation);
    } else {
      this.selectedNation$.next(value);
    }
  }

  simulateGroups(groupGamesPerOpponent: number, groups: GroupTeam[][]): GroupTeam[][] {
    const g: GroupTeam[][] = groups || [];

    for (let i = 0; i < g.length; i++) {
      for (let j = 0; j < g[i].length; j++) {
        // resets
        g[i][j].points = 0;
        g[i][j].gDiff = 0;
        g[i][j].gFor = 0;
        g[i][j].gOpp = 0;
        g[i][j].matchesPlayed = 0;
        g[i][j].matchHistory = {
          qualifiers: g[i][j].matchHistory.qualifiers,
          group: [],
          bracket: [],
        };
        g[i][j].reportCard = {
          grade: null,
          gradeStyle: null,
          gradeSummary: null,
          tournamentFinish: null,
        };
      }
    }
    // go through each group
    // simulate each game and reward that team that many points
    // sort the teams by points
    for (let c = 0; c < groupGamesPerOpponent; c++) {
      let goalsFor = 0;
      let goalsAg = 0;
      for (
        let i = 0;
        i < g.length;
        i++ // for each group
      ) {
        for (let j = 0; j < g[i].length; j++) {
          // for each team
          const team = g[i][j];
          for (let k = j + 1; k < g[i].length; k++) {
            // for each of the other teams play a match
            const otherTeam = g[i][k];
            const match = matchScore(team, otherTeam, false);
            match.winner.matchHistory.group.push({
              match: match,
              opp: match.loser,
            });
            match.loser.matchHistory.group.push({
              match: match,
              opp: match.winner,
            });
            goalsFor = match.goalsFor;
            goalsAg = match.goalsAg;
            if (goalsFor > goalsAg) {
              team.points += 3;
              team.gDiff += goalsFor - goalsAg;
              otherTeam.gDiff += goalsAg - goalsFor;
            } else if (goalsFor < goalsAg) {
              otherTeam.points += 3;
              team.gDiff += goalsFor - goalsAg;
              otherTeam.gDiff += goalsAg - goalsFor;
            } else {
              team.points += 1;
              otherTeam.points += 1;
            }
            team.gFor += goalsFor;
            team.gOpp += goalsAg;
            otherTeam.gFor += goalsAg;
            otherTeam.gOpp += goalsFor;
            team.matchesPlayed++;
            otherTeam.matchesPlayed++;
          }
        }
        g[i].sort((a, b) => b.points - a.points || b.gDiff - a.gDiff || b.gFor - a.gFor || compare(a.name, b.name, true));
      }
    }
    // assign group finishes to teams
    const groupLetters = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H', 'I', 'J', 'K', 'L'].slice(0, groups.length);
    for (let i = 0; i < groupLetters.length; i++) {
      for (let j = 0; j < g[i].length; j++) {
        g[i][j].groupFinish = groupLetters[i] + (j + 1).toString();
      }
    }
    return g;
  }

  simulateBracket(groups: GroupTeam[][]): {
    groupWinners: GroupTeam[];
    bracket: {
      roundOf16: KnockoutRound;
      quarterFinals: KnockoutRound;
      semiFinals: KnockoutRound;
      finals: KnockoutRound;
      roundOf32?: KnockoutRound;
    };
  } {
    const { gWinners, roundOf32 } = roundOf32Helper(groups);

    // assign numbers to letter values, to improve readability of code
    // CAN BE REMOVED
    const [a, b, c, d, e, f, g, h] = [0, 1, 2, 3, 4, 5, 6, 7, 8];

    const roundOf16: KnockoutRound = [
      [gWinners[a][0], gWinners[b][1], matchScore(gWinners[a][0], gWinners[b][1], true)],
      [gWinners[c][0], gWinners[d][1], matchScore(gWinners[c][0], gWinners[d][1], true)],
      [gWinners[e][0], gWinners[f][1], matchScore(gWinners[e][0], gWinners[f][1], true)],
      [gWinners[g][0], gWinners[h][1], matchScore(gWinners[g][0], gWinners[h][1], true)],
      [gWinners[d][0], gWinners[c][1], matchScore(gWinners[d][0], gWinners[c][1], true)],
      [gWinners[b][0], gWinners[a][1], matchScore(gWinners[b][0], gWinners[a][1], true)],
      [gWinners[f][0], gWinners[e][1], matchScore(gWinners[f][0], gWinners[e][1], true)],
      [gWinners[h][0], gWinners[g][1], matchScore(gWinners[h][0], gWinners[g][1], true)],
    ];

    roundOf16.forEach(t => {
      t[2].winner.matchHistory.bracket.push({ match: t[2], opp: t[2].loser });
      t[2].loser.matchHistory.bracket.push({ match: t[2], opp: t[2].winner });
    });

    const quarterFinals: KnockoutRound = [
      [roundOf16[0][2].winner, roundOf16[1][2].winner, matchScore(roundOf16[0][2].winner, roundOf16[1][2].winner, true)],
      [roundOf16[2][2].winner, roundOf16[3][2].winner, matchScore(roundOf16[2][2].winner, roundOf16[3][2].winner, true)],
      [roundOf16[4][2].winner, roundOf16[5][2].winner, matchScore(roundOf16[4][2].winner, roundOf16[5][2].winner, true)],
      [roundOf16[6][2].winner, roundOf16[7][2].winner, matchScore(roundOf16[6][2].winner, roundOf16[7][2].winner, true)],
    ];

    quarterFinals.forEach(t => {
      t[2].winner.matchHistory.bracket.push({ match: t[2], opp: t[2].loser });
      t[2].loser.matchHistory.bracket.push({ match: t[2], opp: t[2].winner });
    });

    const semiFinals: KnockoutRound = [
      [quarterFinals[0][2].winner, quarterFinals[1][2].winner, matchScore(quarterFinals[0][2].winner, quarterFinals[1][2].winner, true)],
      [quarterFinals[2][2].winner, quarterFinals[3][2].winner, matchScore(quarterFinals[2][2].winner, quarterFinals[3][2].winner, true)],
    ];

    semiFinals.forEach(t => {
      t[2].winner.matchHistory.bracket.push({ match: t[2], opp: t[2].loser });
      t[2].loser.matchHistory.bracket.push({ match: t[2], opp: t[2].winner });
    });

    const finals: KnockoutRound = [
      [semiFinals[0][2].winner, semiFinals[1][2].winner, matchScore(semiFinals[0][2].winner, semiFinals[1][2].winner, true)],
      [semiFinals[0][2].loser, semiFinals[1][2].loser, matchScore(semiFinals[0][2].loser, semiFinals[1][2].loser, true)],
    ];
    finals.forEach(t => {
      t[2].winner.matchHistory.bracket.push({ match: t[2], opp: t[2].loser });
      t[2].loser.matchHistory.bracket.push({ match: t[2], opp: t[2].winner });
    });

    // console.log(groups.map(z => z.map(t => [t.name, t.ranking, `Pot${t.pot}`, t.tier, t.region])));

    // TODO: groupWinners might need to change for 48 team tournament
    const groupWinners = gWinners.flat() as GroupTeam[];

    if (roundOf32) {
      return {
        groupWinners,
        bracket: {
          roundOf32,
          roundOf16,
          quarterFinals,
          semiFinals,
          finals,
        },
      };
    }

    return {
      groupWinners,
      bracket: {
        roundOf16,
        quarterFinals,
        semiFinals,
        finals,
      },
    };
  }

  getTournamentAwards(
    bracket: {
      roundOf32?: KnockoutRound;
      roundOf16: KnockoutRound;
      quarterFinals: KnockoutRound;
      semiFinals: KnockoutRound;
      finals: KnockoutRound;
    },
    groups: GroupTeam[][],
    availableRegions?: Region[]
  ): [GroupTeam, GroupTeam, GroupTeam, GroupTeam, GroupTeam, GroupTeam?, GroupTeam?, GroupTeam?, GroupTeam?, GroupTeam?, GroupTeam?] {
    const first = bracket.finals[0][2].winner;
    const second = bracket.finals[0][2].loser;
    const third = bracket.finals[1][2].winner;

    const finalists = bracket.finals.flatMap(m => [m[2].winner, m[2].loser]);

    const overPerformer = finalists.find((t: GroupTeam) => {
      if (t.ranking && availableRegions) {
        return availableRegions.length > 4 ? t.ranking > 12 : t.ranking > 30;
      }
      return 0 > 1;
    })
      ? finalists.reduce((prev, curr) => (prev.rating < curr.rating ? prev : curr))
      : groups.flatMap(group => group.slice(0, 2)).reduce((prev, curr) => (prev.rating < curr.rating ? prev : curr));
    const underPerformer = groups.flatMap(group => group.slice(-2)).reduce((prev, curr) => (prev.rating > curr.rating ? prev : curr));

    return [first, second, third, underPerformer, overPerformer];
  }

  // getCoachInfo(nations: GroupTeam[]): Observable<{
  //   nationality: string;
  //   lastNames: string[];
  //   lastNameUsage: string;
  //   firstNames: string[];
  //   firstInitial: string;
  //   firstNameUsage: string;
  //   totalLastNames: number;
  //   totalFirstNames: number;
  // }>[] {
  //   const updatedNations = [];
