@import 'variables.scss';

.group-stage {
  display: grid;
  justify-content: space-evenly;
  grid-template-columns: repeat(4, 1fr);
  margin: 0 2rem;
  padding: 4rem 0;

  @media screen and (max-width: 1600px) {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
  }

  @media screen and (max-width: 800px) {
    display: flex;
    flex-flow: column;
    margin: 0;
  }

  .group-name {
    text-transform: capitalize;
    font-weight: bold;
    text-align: center;
    padding: 10px 0;
    font-size: 150%;
    font-family: 'aeonik-bold', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
    background-color: lightgray;
    color: black;
    border-radius: 5px;

    @media screen and (max-width: 600px) {
      border-radius: 0;
    }
  }

  .group {
    background-color: $primary;
    color: white;
    display: flex;
    flex-flow: column;
    margin: 10px;
    padding: 5px;
    border-radius: 10px;

    @media screen and (max-width: 600px) {
      margin: 15px 0;
      padding: 0;
      border-radius: 0;
      font-size: 80%;
    }

    table {
      padding: 0 5px;

      th {
        padding: 5px;

        &:first-child {
          text-align: left;
        }
      }

      tr {
        &.nation:last-child {
          border-bottom: none;
        }
      }

      .nation {

        td {
          text-align: center;
          padding: 5px;

          .nation-flag {
            // width: 2.25rem;
            // height: 1.5rem;
            width: 2.25rem;
            height: 2.25rem;
          }

          &.name {
            font-family: 'aeonik-bold', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
            cursor: pointer;
          }
        }
      }
    }
  }
}

.storybook-header {
  font-family: 'Nunito Sans', 'Helvetica Neue', Helvetica, Arial, sans-serif;
  border-bottom: 1px solid rgba(0, 0, 0, 0.1);
  padding: 15px 20px;
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.storybook-header svg {
  display: inline-block;
  vertical-align: top;
}

.storybook-header h1 {
  font-weight: 700;
  font-size: 20px;
  line-height: 1;
  margin: 6px 0 6px 10px;
  display: inline-block;
  vertical-align: top;
}

.storybook-header button + button {
  margin-left: 10px;
}

.storybook-header .welcome {
  color: #333;
  font-size: 14px;
  margin-right: 10px;
}

