    //         font-size: 0.7rem;
    //     }
    //     .pitch-player-info {
    //         font-size: 0.45rem;
    //     }
    //     .player-name {
    //         line-height: normal;
    //     }
    //     .pitch-player-img {
    //         width: 1rem;
    //     }
    // }
    }

}


.lineup-grid {
    display: grid;
    grid-template-columns: 1fr 5fr;
    grid-template-rows: 3rem 1fr;
    grid-template-areas:
    "lineup th"
    "lineup player"
    ;
    background: #424242;
    color: white;
    border-radius: 4px;
    .players-container {
        background: #424242;
        color: white;
    }
    .lineup-headers {
        display: flex;
        justify-content: stretch;
        grid-area: th;
        margin: 0;
        font-weight: bold;
        height: 3rem;
        li {
            // border-bottom: 1px solid black;
            // border-right: 1px solid black;
            flex: 1;
            display: flex;
            align-items: center;
            justify-content: center;
            width: 25%;
            padding: 0.25rem;
        }
        :last-child {
            border-right: none;
        }
        .name-header {
            width: 10vw;
        }
    }
    .starters {
        grid-area: player;
    }
    .bench-container,
    .starters-container {
        text-transform: capitalize;
        border-bottom: none;
        .player-row {
            display: flex;
            margin: 0;
            height: 3rem;
            justify-content: flex-start;
            li {
                display: flex;
                justify-content: center;
                align-items: center;
                flex: 1;
                padding: 0.25rem;
                // border-right: 1px solid black;
                // border-bottom: 1px solid black;
            }
            :last-child {
                border-right: none;
            }
            .alt-positions {
                flex-flow: wrap;
            }
        }
    }

    .lineup-spots {
        margin: 0;
        display: flex;
        flex-flow: column;
        grid-area: lineup;
        ul {
            margin: 0;
        }
        ul:first-child {
            font-weight: bold;
            border-top: none;
        }
        ul:not(:first-child) {
            font-weight: 500;
        }
        ul li {
            padding: 0 0.5rem;
            height: 3rem;
            border-top: 1px solid rgba(242, 242, 242, 0.25);
            border-right: 1px solid rgba(242, 242, 242, 0.25);
            display: flex;
            justify-content: center;
            align-items: center;
        }
    }
}
.expanded-info {
    display: flex;
    justify-content: space-between;
    max-width: 80vw;
    flex-flow: wrap ;
    span {
        display: flex;
        align-items: center;
    }
    span:last-child{
        color: #f8f2cf;
    }
    span.expanded-names {
        flex-flow: wrap;
        span{

            text-transform: none;
        }
        span:not(:last-child) {
            margin-right: 0.5rem;
        }
    }

    img {
        max-width: 2rem;
        margin: 1rem;
    }
}

.nation-name-heading {
    text-transform: capitalize;
}

.arrow-nav {
    cursor: pointer;
}

.scroll-bar,
.scroll-button {
    display: none;
}

@media screen and (max-width: 800px) {
    .scroll-button {
        display: block;
        background-color: #2ab7a9;
        z-index: 11;
        position: sticky;
        top: 0;
    }

    .scroll-bar {
        display: block;
        position: sticky;
        z-index: 10;
        top: 0;
        left: 18px;
        height: 100vh;
        width: 5px;
        background-color: #00ff00;
    }
}


// *** ANGULAR CDK DRAG & DROP classes ***
// Do not move cdk drag classes inside other classes
.cdk-drag-preview {
    box-sizing: border-box;
    color: #000000;
    background-color: #d7d5d5;
    border: 2px inset black;
    border-radius: 4px;
    box-shadow: 0 5px 5px -3px rgba(0, 0, 0, 0.2),
        0 8px 10px 1px rgba(0, 0, 0, 0.14),
        0 3px 14px 2px rgba(0, 0, 0, 0.12)
    ;
    padding: 1rem;
    width: 8vw;
    height: 10vh;
    display: flex;
    flex-flow: column;
    align-items: center;
    .shirtIcon-img {
        height: 4vh;
    }
}
.cdk-drag-animating {
    transition: transform 250ms cubic-bezier(0, 0, 0.2, 1);
}
.cdk-drag-placeholder.lineup-drag-placeholder {
    height: 100%;
    width: 100%;
    background: rgb(160, 160, 160);
    color: #353535;
    transition: transform 250ms cubic-bezier(0, 0, 0.2, 1);
    border: 2px dashed white;
}
.info-paragraph li {
    display: flex;
    align-items: center;
    div:last-child {
        flex: 1;
        display: inline-block;
    }
    .color-code {
        display: inline-block;
        width: 1rem;
        height: 1rem;
        margin-right: 1rem;
    }
}

.natural {
    background-color: #00ff00;
}
.accomplished {
    background-color: #009700;
}
.competent {
    background-color: #699424;
}
.unconvincing {
    background-color: #888800;
}
.awkward {
    background-color: #b36302;
}
.ineffectual {
    background-color: #830000;
}
.natural-placeholder {
    border: ridge 2px #00ff00;
    background-color: #00ff00;
    border-radius: 50% / 10%;
}
.accomplished-placeholder {
    border: ridge 2px #009700;
    background-color: #009700;
    border-radius: 50% / 10%;
}
.competent-placeholder {
    border: ridge 2px #699424;
    background-color: #699424;
    border-radius: 50% / 10%;
}
.unconvincing-placeholder {
    border: ridge 2px #888800;
    background-color: #888800;
    border-radius: 50% / 10%;
}
.awkward-placeholder {
    border: ridge 2px #b36302;
    background-color: #b36302;
    border-radius: 50% / 10%;
}
.ineffectual-placeholder {
    border: ridge 2px #830000;
    background-color: #830000;
    border-radius: 50% / 10%;
}

.cdk-drag-placeholder.pitch-placeholder {
    height: 100%;
    width: 100%;

}

.cdk-drop-list-dragging, .cdk-drag-dragging {
    cursor: grabbing;
}

.cdk-drop-list-receiving > div > div {
        background: #f5f5f5;
        height: 16.5vh;
        border-radius: 50% / 10%;
        display: grid;
        grid-template-columns: 1fr fit-content(50%);
        gap: 3.5px 1.5px;
        align-items: center;
        justify-items: center;
        padding: 10px 3.5px;
        max-height: 10.5rem;
        color: #353535;
        .pitch-player-info {
            grid-column: 1;
            font-size: 0.75rem;
            line-height: normal;
            &:first-of-type {
                font-size: 1.25rem;
            }
        }
        .pitch-player-img {
            width: clamp(15px, 1.7vw, 30px);
            grid-column: 1;
        }
        .player-name {
            // font-size: 0.6rem;
            grid-column: 2;
            grid-row: 3 / 5;

        }
        .player-face {
            grid-column: 2;
            grid-row: 1 / 3;
            width: 70%;

        }
    @media screen and (max-width: 800px) {
        font-size: 0.5rem;
        line-height: normal;
        height: 12vh;
        width: clamp(2.25rem, 7.5vw, 10rem);
        display: flex;
        flex-flow: column;
        justify-content: space-between;
        align-items: center;
        .player-face {
            display: none;
        }
        .pitch-player-img {
            width: 0.8rem;
        }
        .pitch-player-info:first-of-type {
            font-size: 0.75rem;
        }
    }
}

