      }
    }
  }

  .headings48 {
    display: grid;
    grid-template-columns: repeat(5, 1fr);
    grid-gap: 1rem;
    font-size: 2.25rem;

    @media screen and (max-width: 1600px) {
      grid-template-columns: repeat(5, 100%);
      gap: 0;
    }

    li {
      display: flex;
      justify-content: center;

      @media screen and (max-width: 1600px) {
        margin: 0 2rem;
      }
    }
  }

  .grid32 {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    grid-template-rows: repeat(16, 3.5rem);
    margin: 0rem 2rem;
