    gap: 1rem;

    @media screen and (max-width: 1200px) {
      grid-template-columns: repeat(4, 100%);
      grid-template-rows: repeat(16, auto);
      gap: 0;
      margin: 0;
    }

    .round {
      display: grid;
      grid-row: span 16;
      align-items: center;
      scroll-snap-align: center;
