import { Component, HostListener, OnInit } from '@angular/core';
import { GroupTeam } from 'app/models/nation.model';
import { originalOrder } from '@shared/utils';
import { RegionName, Tournament } from 'app/simulation/simulation.model';
import { findTeamInTournament, getDisplayRating, getGradeStyle, getGradeSummary } from 'app/simulation/simulation.utils';
import { SimulationService } from 'app/simulation/simulation.service';
import { UntilDestroy, untilDestroyed } from '@ngneat/until-destroy';
import { combineLatest } from 'rxjs';

@UntilDestroy()
@Component({
  selector: 'app-nation-dialog',
  templateUrl: './nation-dialog.component.html',
  styleUrls: ['./nation-dialog.component.scss'],
})
export class NationDialogComponent implements OnInit {
  service: SimulationService;
  isApp = false;
  nation: GroupTeam | null = null;
  tournament: Tournament | null = null;
  screenWidth: number;
  originalOrder = originalOrder;
  getDisplayRating = getDisplayRating;

  rounds = ['Round of 16', 'Quarter Finals', 'Semi Finals', 'Finals / Third Place Match'];
  stages = [
    {
      heading: 'Qualifiers',
      prop: 'qualifiers',
    },
    {
      heading: 'Group Stage',
      prop: 'group',
    },
    {
      heading: 'Knockout Stage',
      prop: 'bracket',
    },
  ];

  constructor(service: SimulationService) {
    this.screenWidth = window.innerWidth;
    this.getScreenSize();
    this.service = service;
    this.isApp = service.checkForApp();

    service.tournament$.pipe(untilDestroyed(this)).subscribe(t => (this.tournament = t));

    combineLatest([service.tournament$, service.selectedNation$])
      .pipe(untilDestroyed(this))
      .subscribe(([tournament, nation]) => {
        this.tournament = tournament;
        this.nation = nation;
        if (nation && !nation.reportCard.grade && tournament?.groupWinners) {
          this.getNationReportCard(nation);
        }
      });
  }

  @HostListener('window:resize', ['$event'])
  getScreenSize() {
    this.screenWidth = window.innerWidth;
  }

  ngOnInit(): void {
    this.rounds = this.tournament?.bracket?.roundOf32
      ? ['Round of 32', 'Round of 16', 'Quarter Finals', 'Semi Finals', 'Finals / Third Place Match']
      : ['Round of 16', 'Quarter Finals', 'Semi Finals', 'Finals / Third Place Match'];
  }

  changeSelectedNation(nation?: GroupTeam) {
    if (nation === this.nation || !this.tournament?.groups) {
      return;
    }
    const nationWithStats = this.tournament.groups.flat().find(a => a.name === nation?.name);
    if (nationWithStats) {
      this.service.changeSelectedNation(nationWithStats);
      return;
    } else if (nation) {
      this.service.changeSelectedNation(nation);
      return;
    }
    this.service.changeSelectedNation(null);
  }

  getNationReportCard(team: GroupTeam) {
    const { tournament } = this;
    if (!team.ranking || !tournament?.groups) {
      return;
    }

    const nation = findTeamInTournament(tournament.groups, team) ?? team;
    let rankingTiers: Array<string | number> = ['s', 'a', 'b', 'c', 'd', 'e', 'f', 'g'];
    if (tournament.availableRegions) {
      const availableRegions = tournament.availableRegions.map(r => r.value);

      if (availableRegions.length === 1) {
        if (availableRegions[0] === RegionName.uefa) {
          rankingTiers = [5, 10, 20, 35, 50, 80, 100, 400];
        } else if (availableRegions[0] === RegionName.caf) {
          rankingTiers = [30, 60, 80, 110, 120, 130, 140, 400];
        }
      } else if (availableRegions.length < 5) {
        if (!availableRegions.includes(RegionName.uefa) && !availableRegions.includes(RegionName.conmebol)) {
          rankingTiers = [30, 60, 80, 110, 120, 130, 140, 400];
        } else if (
          !availableRegions.includes(RegionName.afc) &&
          !availableRegions.includes(RegionName.afc) &&
          !availableRegions.includes(RegionName.concacaf)
        ) {
          rankingTiers = [5, 10, 20, 35, 50, 80, 100, 400];
        } else {
          rankingTiers = [8, 18, 25, 32, 50, 70, 100, 400];
        }
      }
    }
    rankingTiers.every((r, i) => {
      if (nation.ranking && ((typeof r === 'number' && nation.ranking <= r) || nation.tier === r || nation.pot === r)) {
        const { grade, result } = this.calcGrade(nation, i);
        nation.reportCard.grade = grade;
        nation.reportCard.tournamentFinish = result;
        nation.reportCard.gradeStyle = getGradeStyle(grade);
        nation.reportCard.gradeSummary = getGradeSummary(nation);
        return false;
      }
      return true;
    });
  }

  calcGrade(
    nation: GroupTeam,
    rankingIndex: number
  ): {
    grade: string;
    result: string;
  } {
    const { tournament } = this;
    if (tournament?.groupWinners === undefined || tournament.bracket === undefined || tournament.awards === undefined) {
      return { grade: 'n/a', result: 'Did Not Qualify' };
    }
    let gradeArr = ['f', 'f', 'f', 'f', 'f', 'f', 'f', 'f'];
    let result = '';
    if (nation.matchesPlayed < 3) {
      gradeArr = Array(8).fill('n/a');
      result = 'Did Not Qualify';
    } else if (nation.points < 3 && nation.gDiff < -3) {
      gradeArr = ['f', 'f', 'f', 'd', 'c', 'c', 'c', 'b'];
      result = 'Group Stage';
    } else if (nation.points < 3) {
      gradeArr = ['f', 'f', 'd', 'c', 'c', 'b', 'b', 'a'];
      result = 'Group Stage';
    } else if (!tournament.groupWinners.includes(nation)) {
      gradeArr = ['f', 'f', 'c', 'c', 'b', 'b', 'a', 's'];
      result = 'Group Stage';
    } else if (!tournament.bracket.quarterFinals.flat().includes(nation)) {
      gradeArr = ['d', 'd', 'b', 'a', 'a', 's', 's', 's'];
      [result] = this.rounds;
    } else if (!tournament.bracket.semiFinals.flat().includes(nation)) {
      gradeArr = ['c', 'c', 'b', 'a', 's', 's', 's', 's'];
      result = 'Quarter Finals';
    } else if (tournament.bracket.finals[1].includes(nation) && tournament.awards[2] !== nation) {
      gradeArr = ['b', 'b', 'a', 's', 's', 's', 's', 's'];
      result = '4th Place';
    } else if (tournament.awards[2] === nation) {
      gradeArr = ['b', 'a', 'a', 's', 's', 's', 's', 's'];
      result = '3rd Place';
    } else if (tournament.awards[1] === nation) {
      gradeArr = ['b', 'a', 's', 's', 's', 's', 's', 's'];
      result = 'Runner Up';
    } else if (tournament.awards[0] === nation) {
      gradeArr = ['a', 'a', 's', 's', 's', 's', 's', 's'];
      result = 'Winner';
    } else {
      gradeArr = Array(8).fill('n/a');
      result = 'Did Not Qualify';
    }
    return {
      grade: gradeArr[rankingIndex],
      result,
    };
  }

  getCoachRating(rating: number): string {
    if (rating > 96) {
      return 'S+';
    } else if (rating > 92) {
      return 'S';
    } else if (rating > 89) {
      return 'S-';
    } else if (rating > 86) {
      return 'A+';
    } else if (rating > 82) {
      return 'A';
    } else if (rating > 79) {
      return 'A-';
    } else if (rating > 76) {
      return 'B+';
    } else if (rating > 72) {
      return 'B';
    } else if (rating > 69) {
      return 'B-';
    } else if (rating > 66) {
      return 'C+';
    } else if (rating > 62) {
      return 'C';
    } else if (rating > 59) {
      return 'C-';
    } else if (rating > 56) {
      return 'D+';
    } else if (rating > 52) {
      return 'D';
    } else if (rating > 49) {
      return 'D-';
    } else if (rating > 46) {
      return 'E+';
    } else if (rating > 42) {
      return 'E';
    } else if (rating > 39) {
      return 'E-';
    } else {
      return 'F';
    }
  }
}

styleUrlsData.data:
:host {
  position: fixed;
  left: 0;
  top: 0;
  right: 0;
  bottom: 0;
  background-color: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  opacity: 0;
  transition: all 0.3s ease-in-out;
  pointer-events: none;

  &.show {
    opacity: 1;
    pointer-events: visible;

    .container {
      transform: translateY(0);
    }
  }

  @media screen and (max-width: 1000px) {
    font-size: 80%;
  }
}

h3 {
  font-family: 'aeonik-bold', system-ui, -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
}

.container {
  width: 50%;
  background-color: #4F4F4F;
  color: white;
  transform: translateY(-200px);
  transition: all 0.3s ease-in-out;
  max-height: 100vh;
  overflow-y: auto;

  @media screen and (max-width: 1250px) {
    width: 95%;
  }

  @media screen and (max-width: 600px) and (any-pointer: coarse) {
    width: 100%;
    height: 100vh;
    margin-top: 25%;
    padding-bottom: 1rem;
    &.app {
      margin-top: 0;
    }
  }

  .header {
    display: flex;
    flex-direction: column;
    padding: 1rem;

    .name-row {
      display: flex;
      justify-content: space-between;
      padding-bottom: 1rem;
      .nation {
        display: flex;
        align-items: center;

        @media screen and (max-width: 600px) {
          flex-wrap: wrap;
        }

        .nation-flag {
          font-size: 5rem;
          display: flex;
          justify-content: center;
          align-items: center;
          max-width: 6rem;
          height: 3rem;
          margin-right: 0.5rem;

          @media screen and (max-width: 600px) {
            font-size: 4rem;
          }
        }

        h2 {
          font-size: 3.5rem;
          margin: 0 0.5rem;
          text-transform: uppercase;
          display: flex;
          align-items: center;

          @media screen and (max-width: 1250px) {
            font-size: 2.25rem;
          }

          @media screen and (max-width: 600px) {
            font-size: 2rem;
          }
        }


      }
      .exit-button {
        font-size: 300%;
        cursor: pointer;
        flex: 1;
        display: flex;
        justify-content: flex-end;
      }
    }





    .stats-row {
      display: flex;
      flex-wrap: nowrap;
      justify-content: space-between;
      >div {
        display: flex;
        >div {
          display: flex;
          flex-direction: column;
          align-items: center;
          justify-content: space-evenly;
          padding: 0 0.5rem;

          h3 {
            font-size: 2rem;

            @media screen and (max-width: 1000px) {
              font-size: 1.25rem;
            }

            @media screen and (max-width: 425px) {
              font-size: 1rem;
            }
          }
          span {
            @media screen and (max-width: 425px) {
              font-size: 0.75rem;
            }
          }

          &.first {
            padding: 0 2rem 0 0;
            @media screen and (max-width: 425px) {
              padding: 0;
            }
          }
        }

      }

    }
  }

  .is-debuffed {
    color: #ef3f18;
  }

  .is-buffed {
    color: #83e356;
  }

  .small-rating {
    font-size: 0.8rem;
    color: #ffffff80
  }

  .body {
    display: flex;
    flex-direction: column;
    padding: 10px;
    border-top: 1px solid #ddd;
    min-height: 400px;

    h3 {
      font-size: 1.5rem;
    }

    .flex-container {
      display: flex;
      flex-flow: row wrap;
    }

    .report-card {
      padding-top: 10px;

      >* {
        margin-bottom: 10px;
        display: block;
      }

      span {
        font-style: italic;
        font-size: 1rem;
      }

      .grade {
        font-size: 2.5rem;
        text-transform: uppercase;

        @media screen and (max-width: 600px) {
          font-size: 1.25rem;
        }

        &.good-grade {
          color: #00ff00;
        }

        &.ok-grade {
          color: yellow;
        }

        &.bad-grade {
          color: red;
        }
      }
    }

    .matches {
      display: flex;
      flex-direction: column;
      margin: 10px;

      @media screen and (max-width: 600px) {
        width: 100%;
        margin: 0;
      }

      .match-stages {
        margin-top: 5px;
        margin-bottom: 15px;

        @media screen and (max-width: 600px) {
          margin-bottom: 0;
        }
      }

      .match-row {
        display: flex;
        padding: 10px 0;
        align-items: center;

        >span {
          margin-right: min(4vw, 50px);
        }

        .team-name {
          max-width: 6.5rem;
        }

        .score {
          max-width: 3.5rem;
          text-decoration: underline dotted;
          position: relative;
          cursor: default;
          .score-tooltip {
            visibility: hidden;
            background-color: black;
            color: #fff;
            text-align: center;
            padding: 10px 10px;
            border-radius: 6px;

            /* Position the tooltip text */
            position: absolute;
            z-index: 1;
            width: 200px;
            top: 100%;
            right: 50%;
            margin-right: -100px; /* Use half of the width (120/2 = 60), to center the tooltip */

            .title {
              display: block;
              font-size: 115%;
              margin-bottom: 10px;
            }

            .flex {
              display: flex;
              justify-content: space-between;
              >div {
                display: flex;
                flex-flow: column;
                text-align: left;
              }
            }
          }

          &:hover .score-tooltip {
            visibility: visible;
          }
        }

        .round {
          width: 5rem;
        }

        .nation-flag {
          width: 3rem;
          height: 3rem;
          margin-right: 1rem;
          cursor: pointer;

          &.disabled {
            cursor: not-allowed;
          }
        }
      }
    }

    .personel {
      margin: 10px;

      h3 {
        margin-bottom: 10px;
      }

      th, td, summary {
        text-align: left;
        padding: 5px;
      }

      summary {
        cursor: pointer;
      }
    }


  }
}

