() => {
  const newArr = nationsJson;
  const nations: Nation[] = [];

  Object.values(newArr).forEach(t => {
    if (t.nations) {
      nations.push(...(t.nations as Nation[]));
    }
  });

  return nations;
}

(pts: GroupTeam[][], nbrOfGroups: number, availableRegions: Region[]) => {
  const regionValues = availableRegions.map(r => r.value);
  const allTeams: GroupTeam[] = pts.flatMap((p, i) => {
    const team = p.map(x => ({ ...x, pot: i + 1 }));
    if (availableRegions.length > 1) {
      team.sort(({ region: a }, { region: b }) => compare(a, b, true));
    }
    return team;
  });
  const groups: GroupTeam[][] = Array.from({ length: nbrOfGroups }, _ => []);
  for (let i = 0; i < allTeams.length; i++) {
    // for each team in the draw
    const team = allTeams[i];
    // console.log('TEAM', team.region, team.pot);
    const candidateGroups = groups.filter(
      // return each group that returns true to ...
      group => {
        if ((availableRegions.length > 4 && !regionValues.includes(RegionName.ofc)) || availableRegions.length > 5) {
          // check the group has less teams than is needed in each group
          return (
            group.length < allTeams.length / nbrOfGroups &&
            group.every(member => {
              // console.log('MEMBER', member.region, member.pot);
              return (
                member.pot !== team.pot &&
                (team.region !== RegionName.uefa
                  ? group.every(m => m.region !== team.region) // checking that every member of this group does not match region
                  : group.filter(m => m.region === RegionName.uefa).length < 2)
              );
            })
          ); // if team is uefa, the group can have a uefa team
        }
        return group.length < allTeams.length / nbrOfGroups && group.every(member => member.pot !== team.pot);
      }
    );
    candidateGroups[Math.floor(Math.random() * candidateGroups.length)].push(team);
  }
  console.log(
    'groups',
    groups.map(g => g.map(t => `${t.name} ${t.region}`))
  );
  return groups;
}

(hostNations: GroupTeam[], nation: Nation): GroupTeam => {
  // random nation values
  let min = 0;
  let max = 0;

  switch (nation.nationTier) {
    case 's':
      min = 80;
      max = 100;
      break;
    case 'a':
      min = 70;
      max = 95;
      break;
    case 'b':
      min = 65;
      max = 88;
      break;
    case 'c':
      min = 60;
      max = 88;
      break;
    case 'd':
      min = 55;
      max = 80;
      break;
    case 'e':
      min = 40;
      max = 78;
      break;
    case 'f':
      min = 30;
      max = 70;
      break;
    case 'g':
      min = 25;
      max = 55;
      break;
    default:
      min = 25;
      max = 55;
      break;
  }
  const attRating = getRandFloat(min, max);
  const midRating = getRandFloat(min, max);
  const defRating = getRandFloat(min, max);
  const penRating = getRandFloat(min, max);
  return {
    ...nation,
    gDiff: 0,
    gFor: 0,
    gOpp: 0,
    points: 0,
    matchesPlayed: 0,
    tier: nation.nationTier,
    startingRating: {
      att: attRating,
      mid: midRating,
      def: defRating,
      pen: penRating,
    },
    dynamicRating: {
      att: attRating,
      mid: midRating,
      def: defRating,
      pen: penRating,
    },
    isBuffed: {
      att: false,
      mid: false,
      def: false,
      pen: false,
    },
    isDebuffed: {
      att: false,
      mid: false,
      def: false,
      pen: false,
    },
    currentBuffs: {
      att: [],
      mid: [],
      def: [],
      pen: [],
    },
    rating: (attRating + midRating + defRating) / 3,
    ranking: 0,
    attRanking: 0,
    midRanking: 0,
    defRanking: 0,
    matchHistory: {
      qualifiers: [],
      group: [],
      bracket: [],
    },
    reportCard: {
      grade: null,
      gradeStyle: null,
      gradeSummary: null,
      tournamentFinish: null,
    },
    homeTeam: hostNations.map(t => t.name).includes(nation.name) ? true : false,
    region: nation.region as RegionName,
  };
}

