(first: string[], a: string, b: string) => {
  let returnValue;
  if (first.includes(a)) {
    returnValue = -1;
  } else if (first.includes(b)) {
    returnValue = 1;
  } else if (a < b) {
    returnValue = -1;
  } else if (b < a) {
    returnValue = 1;
  } else {
    returnValue = 0;
  }
  return returnValue;
}

