({ age, potentialAbility, currentAbility }: Season): number => {
  const potDiff = potentialAbility - currentAbility;
  if (age < 18) {
    return potDiff / 10 - 4;
  } else if (age < 24) {
    return potDiff / 20;
  } else if (age < 32) {
    return potDiff / 30;
  } else {
    return -potDiff / 10;
  }
}

